import React from "react";
import strings from "./strings";
import "./styles.css";

const getInput = (label) => {
  return (
    <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
      <label>{label}</label>
      <input type="text" />
    </div>
  );
};

const getContactRow = (contactDetails) => {
  return (
    <div style={{ display: "flex" }}>
      {getInput(contactDetails.firstName)}
      {getInput(contactDetails.lastName)}
    </div>
  );
};

export default function Contact() {
  return (
    <div id="contact-wrapper">
      <div id="contact-form-wrapper">
        <div id="contact-title-wrapper">{strings.getInTouch}</div>
        {getContactRow({
          firstName: { label: strings.firstName, type: "text" },
        })}
        {/* <div style={{ display: "flex" }}>
          {getInput(strings.firstName)}
          {getInput(strings.lastName)}
        </div> */}
        <div style={{ display: "flex" }}>
          {getInput(strings.email)}
          {getInput(strings.phone)}
        </div>
        <div style={{ display: "flex" }}>{getInput(strings.message)}</div>
      </div>
      <div id="contact-submit-wrapper">
        <div>{strings.submit}</div>
      </div>
    </div>
  );
}
