import React from "react";
import "./styles.css";
import strings from "./strings";
import skylineImg from "../../Assets/Skyline.png";
import Typewriter from "typewriter-effect";

export default function Main() {
  const screenWidth = window.innerWidth;
  // 1440
  let fontSize = 100;
  if (screenWidth < 400) {
    console.log("Less than 400");
    fontSize = 50;
  } else if (screenWidth < 500) {
    fontSize = 60;
    console.log("Less than 500");
  } else if (screenWidth < 600) {
    fontSize = 65;
    console.log("Less than 600");
  } else if (screenWidth < 700) {
    fontSize = 60;
    console.log("Less than 700");
  } else if (screenWidth < 800) {
    fontSize = 65;
    console.log("Less than 800");
  } else if (screenWidth < 900) {
    fontSize = 50;
    console.log("Less than 900");
  } else if (screenWidth < 1000) {
    fontSize = 50;
    console.log("Less than 1000");
  } else if (screenWidth < 1100) {
    fontSize = 50;
    console.log("Less than 1100");
  } else if (screenWidth < 1200) {
    fontSize = 50;
    console.log("Less than 1200");
  }
  return (
    <div id="main-wrapper">
      <img src={skylineImg} alt={strings.skylineAlt} id="main-img" />
      <div id="main-overlay-wrapper">
        <div id="main-quote" style={{ fontSize }}>
          {/* <Typewriter
            options={{
              strings: [strings.weMakePeople],
              autoStart: true,
              loop: true,
            }}
          /> */}
          <div>{strings.weMakePeople}</div>
        </div>
        <div id="main-btn-wrapper">
          <button
            id="main-contact-btn"
            onClick={() =>
              document
                .getElementById("contact-wrapper")
                .scrollIntoView({ behavior: "smooth" })
            }
          >
            {strings.getInTouch}
          </button>
        </div>
      </div>
    </div>
  );
}
